        //             console.error('Share not supported');
        //           }
        //         } else {
        //           console.error('Blob is null');
        //         }
        //       });
        //     });
        //   }
        // }
        // --------------Text Messgae Send------------------------------------
        this.formData = {};
        this.Summary = '';
        this.email = '';
        this.message = '';
        this.Source = '';
        this.captionText = '';
        this.imageUrl = 'https://i.ibb.co/jR70VKs/screenshot-12.png[/img';
    }
    onReady(editor) {
        const element = editor.ui.getEditableElement();
        const parent = element.parentElement;
        parent.insertBefore(editor.ui.view.toolbar.element, element);
        console.log(editor);
    }
    changeImg2(event) {
        let file = event.target.files[0];
        console.log(event, file);
        // base64 format -- file
        let fileRead = new FileReader();
        fileRead.readAsDataURL(file);
        fileRead.onload = () => {
            this.imageUrl2 = fileRead.result;
            console.log(this.imgPath);
        };
    }
    changeImg(event) {
        let file = event.target.files[0];
        console.log(event, file);
        // base64 format -- file
        let fileRead = new FileReader();
        fileRead.readAsDataURL(file);
        fileRead.onload = () => {
            this.imgPath = fileRead.result;
            console.log(this.imgPath);
        };
    }
    toggleInput() {
        this.showInput = !this.showInput;
    }
    toggleInput2() {
        this.showInputck = !this.showInputck;
    }
    cktext(event) {
        console.log(event);
        this.textdata = this.sanitizer.bypassSecurityTrustHtml(this.textdata);
    }
    submitForm() {
        console.log('Summary:', this.Summary);
        console.log('Email:', this.email);
        console.log('Message:', this.message);
        console.log('Source:', this.Source);
    }
    // --------------Screenshot----
    screenshot() {
        const captureElement = document.getElementById('capture');
        if (captureElement) {
            html2canvas(captureElement).then(canvas => {
                const link = document.createElement('a');
                link.href = canvas.toDataURL('image/png');
                link.download = 'screenshot.png';
                link.click();
            });
        }
    }
    captureAndShare() {
        const captureElement = document.getElementById('capture');
        if (captureElement) {
            html2canvas(captureElement).then(canvas => {
                const imageDataURL = canvas.toDataURL('image/png');
